mapper.insert:
INSERT INTO t_emp_rel_com
(
t_rel_id, t_emp_id, t_com_id, status
)VALUES (
    #{relId},
    #{empId},
    #{comId},
    #{status}
)

mapper.delete:
DELETE FROM  t_emp_rel_com
WHERE 1=1
and t_rel_id = #{relId}

mapper.update:
UPDATE t_emp_rel_com
SET
 status = #{status}
WHERE
  t_rel_id = #{relId}
